import React from 'react'
import Track from './Track'
import songs from '../songs'

class MusicSection extends React.Component {
  constructor() {
    super()
    this.state = {
      src: "",
      paused: true,
      duration: 10,
      currentTrackNumber: "",
      currentTime: 1
    }

    this.player = React.createRef()
  }


  handleSetSrc = (id) => {
    return () => {
      this.setState({
        src: songs[id].src,
        currentTrackNumber: id,
      }, () => {
        this.setState({
          duration: this.player.current.duration,
        }, () => {
          if (this.player.current.paused) {
            this.player.current.play()
            this.setState({
              paused: false
            })
          } else {
            this.player.current.pause()
            this.setState({
              paused: true
            })
          }
        }) //when done setting state do this

      }) 
    }
  } 

  handleTimeUpdate = (event) => {
    this.setState({
      currentTime: event.target.currentTime
    })
  }

  render() {
    const list = songs.map((item, index) => {
      return(
        <Track 
          currentTime={this.state.currentTime}
          currentTrackNumber={this.state.currentTrackNumber}
          paused={this.state.paused}
          duration={this.state.duration}
          title={this.state.title}
          key={index}
          id={index}
          setSrc={this.handleSetSrc}
        />
      )
    })
    return(
      <div className="tracks" >
        <audio 
          ref={this.player}
          src={this.state.src}
          onTimeUpdate={this.handleTimeUpdate}
        />

        {list}

      </div>
    )
  }
}

export default MusicSection
