import React from 'react'
import Track from './Track'
import songs from '../songs'

class MusicSection extends React.Component {
  constructor() {
    super()
    this.state = {
      src: ""
    }

    this.player = React.createRef()
  }

  componentDidUpdate(prevProps, prevState ) {
    if(this.state.src !== prevState.src) {
      this.player.current.pause()
      this.player.current.play()
    }
  }

  handleSetSrc = (id) => {
    return () => {
      console.log(songs[id].src)
      this.setState({
        src: songs[id].src
      })
      // console.log(this.state.src)
      // if (this.player.current.src || this.player.current.paused) {
      //   this.player.current.play()
      // } else {
      //   this.player.current.pause()
      // }
    }
  }


  render() {
    const list = songs.map((item, index) => {
      return(
        <Track 
          key={index}
          id={index}
          setSrc={this.handleSetSrc}
        />
      )
    })
    return(
      <div className="tracks" >
        <audio 
          ref={this.player}
          src={this.state.src}
        />

        {list}

      </div>
    )
  }
}

export default MusicSection
