import React from 'react'
import track from '../audio/Deep.mp3'

function Track() {

  let bg = document.getElementsByClassName("bg");
  let tracks = document.getElementsByTagName("audio");
  let scrubberIds = [];
  let scrubbers = document.getElementsByClassName("scrubber");


  for (let i = 0; i < bg.length; i++) {
    bg[i].addEventListener("click", () => {
      if (tracks[i].paused === true) { //if track is paused
        for (let j = 0; j < tracks.length; j++) { //pause all tracks
          tracks[j].pause();
        }
        tracks[i].play(); //play selected track
        barStart(tracks[i], i);  //start the progress bar of selected track
      } else {
        // console.log(tracks[i]);
        tracks[i].pause(); //else pause selected track
        // barStop();
      }
      // console.log(bg[i].firstChild.nextSibling.classList.contains("play"));
      bg[i].firstChild.firstChild.classList.toggle("paused"); // add or remove class name "paused" for styling
      for (let j = 0; j < tracks.length; j++) {  //styling classes
        if (bg[j].firstChild.firstChild.classList.contains("playing")) {  //if something is playing turn it off
          bg[j].firstChild.firstChild.classList.toggle("playing"); 
          bg[j].firstChild.firstChild.classList.toggle("paused");
        }
      }
      bg[i].firstChild.firstChild.classList.toggle("playing");  //then turn on selected track
      if (tracks[i].paused === true) { //if the track audio is paused switch class to paused
          bg[i].firstChild.firstChild.classList.toggle("playing"); 
          bg[i].firstChild.firstChild.classList.toggle("paused");
      }
    } );
  }


  //add play


  // progress bar
  function barStart(track, index) {

    scrubberIds[index] = setInterval(frame, 100); //creates an id with respect to track number

    function frame() {
      if (track.ended || track.paused === true) {
        clearInterval(scrubberIds[index]);
      } else {
        // console.log(track.currentTime);
          scrubbers[index].setAttribute("value", 100*track.currentTime/track.duration);
      }
    }
  }


  for (let i = 0; i < scrubbers.length; i++) {
    scrubbers[i].addEventListener("click", progressLocation);
  }

  function progressLocation(event) {

    let relativeTrack = event.srcElement.previousElementSibling.firstElementChild.firstElementChild;
    let duration = relativeTrack.duration;
    let scrollAmount = event.pageX - event.srcElement.offsetLeft; // max is width of .bg = 200
    relativeTrack.currentTime = scrollAmount * duration / 200;
  }

  function play(event) {
    console.log("click")

  }

  return(
    <div className="player">
      <div className="bg" onClick={play}>
        <div className="paused">
          <audio>
            <source src={track} type="audio/ogg" />
          Your browser does not support the audio element.
          </audio>
        </div>
      </div>
        <progress className="scrubber" value="0" max="100"></progress>
    </div>
  )
}

export default Track
