import React from 'react'
import Track from './Track'
import songs from '../songs'

class MusicSection extends React.Component {
  constructor() {
    super()
    this.state = {
      src: ""
    }

    this.player = React.createRef()
  }

  componentDidUpdate(prevProps, prevState ) { //runs whenever audio src is set a to new one by clicking on it
    if(this.state.src !== prevState.src) {
      if (this.player.current.paused) {
        this.player.current.play()
      }
    }
  }

  handleSetSrc = (id) => {
    return () => {
      this.setState({
        src: songs[id].src
      })
        this.player.current.pause()
    }
  }


  render() {
    const list = songs.map((item, index) => {
      return(
        <Track 
          key={index}
          id={index}
          setSrc={this.handleSetSrc}
        />
      )
    })
    return(
      <div className="tracks" >
        <audio 
          ref={this.player}
          src={this.state.src}
        />

        {list}

      </div>
    )
  }
}

export default MusicSection
