import React from 'react'
import Track from './Track'
import track1 from '../audio/Deep.mp3'
import track2 from '../audio/Paprika.mp3'

class MusicSection extends React.Component {
  constructor() {
    super()
    this.state = {
      paused: [true, true, true, true]
    }
  }

  handlePauseAll = () => {
    console.log("pause all")
  }

  handleChangeState = (id) => {
    console.log("change")
    this.setState(prevState => {
      return {
        paused: ""
      }
    })
  }

  render() {
    return(
      <div className="tracks">
        <Track 
          pauseAll={this.handlePauseAll}
          path={track1} 
          data={this.state}
          id="1"
          changeState={this.handleChangeState}
        />
        <Track 
          pauseAll={this.handlePauseAll}
          path={track2} 
          data={this.state}
          id="2"
          changeState={this.handleChangeState}
        />
        <Track />
        <Track />
      </div>
    )
  }
}

export default MusicSection
