import React from 'react'
import Track from './Track'
import track1 from '../audio/Deep.mp3'
import track2 from '../audio/Paprika.mp3'

class MusicSection extends React.Component {
  constructor() {
    super()
    this.state = {
      paused: [true, true, true, true],
      nodes: []
    }
  }

  handlePauseAll = () => {
    let allTrue = [];
    for (let i = 0; i < this.state.paused.length; i++) {
      allTrue[i] = true
    }
    for (let i = 0; i < this.state.nodes.length; i++) {
      // this.state.nodes[i].stop()
      console.log(this.state.nodes[i])
    }
    this.setState({
      paused: allTrue
    })
  }

  handleChangeState = (id) => {
    console.log("change")

    let shallowState = [...this.state.paused]
    shallowState[id] = !shallowState[id]
    this.setState({
      paused: shallowState
    })


  }

  handleLiftNode = (node) => {
    this.setState(prevState => {
      let addNode = [...prevState.nodes]
      addNode.push(node)
      return {
        nodes: addNode
      }
    })
  }

  render() {
    return(
      <div className="tracks">
        <Track 
          pauseAll={this.handlePauseAll}
          path={track1} 
          data={this.state}
          id="1"
          changeState={this.handleChangeState}
          liftTheAudioNode={this.handleLiftNode}
        />
        <Track 
          pauseAll={this.handlePauseAll}
          path={track2} 
          data={this.state}
          id="2"
          changeState={this.handleChangeState}
          liftTheAudioNode={this.handleLiftNode}
        />
        <Track />
        <Track />
      </div>
    )
  }
}

export default MusicSection
